/* Container styling with rounded corners */
.s2w-slider-container {
  position: relative;
  width: 100%;
  max-width: 800px;
  height: 400px;
  margin: 0 auto;
  overflow: hidden;
  border-radius: 15px;
  /* 15px rounded corners */
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

/* Track that holds the slides */
.s2w-slider-track {
  display: flex;
  height: 100%;
  transition: transform 0.5s ease-in-out;
}

/* Individual Slide */
.s2w-slide {
  min-width: 100%;
  height: 100%;
}

.s2w-slide img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

/* Navigation Arrows */
.s2w-arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: rgba(0, 0, 0, 0.5);
  /* 0.5 opacity circle */
  color: white;
  /* White arrow */
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  /* Circle shape */
  font-size: 18px;
  cursor: pointer;
  z-index: 10;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.3s;
}

.s2w-arrow:hover {
  background-color: rgba(0, 0, 0, 0.8);
}

.s2w-prev {
  left: 20px;
}

.s2w-next {
  right: 20px;
}

