document.addEventListener('DOMContentLoaded', () => {
  // Updated selectors to match s2w scope
  const track = document.querySelector('.s2w-slider-track');
  const slides = document.querySelectorAll('.s2w-slide');
  const nextBtn = document.querySelector('.s2w-next');
  const prevBtn = document.querySelector('.s2w-prev');

  let currentIndex = 0;
  const totalSlides = slides.length;
  let autoSlideInterval;

  // Function to move the slider
  function updateSlider() {
    const amountToMove = -currentIndex * 100;
    track.style.transform = `translateX(${amountToMove}%)`;
  }

  // Go to next slide
  function nextSlide() {
    currentIndex++;
    if (currentIndex >= totalSlides) {
      currentIndex = 0;
    }
    updateSlider();
  }

  // Go to previous slide
  function prevSlide() {
    currentIndex--;
    if (currentIndex < 0) {
      currentIndex = totalSlides - 1;
    }
    updateSlider();
  }

  // Timer Reset Logic
  function resetTimer() {
    clearInterval(autoSlideInterval);
    startTimer();
  }

  function startTimer() {
    autoSlideInterval = setInterval(nextSlide, 3000); // 3 seconds
  }

  // Event Listeners
  nextBtn.addEventListener('click', () => {
    nextSlide();
    resetTimer();
  });

  prevBtn.addEventListener('click', () => {
    prevSlide();
    resetTimer();
  });

  // Initialize
  startTimer();
});
