/* Container styling with rounded corners */
.s2w-slider-container {
  position: relative;
  width: 100%;
  max-width: 800px;
  height: 400px;
  margin: 0 auto;
  overflow: hidden;
  border-radius: 15px;
  /* 15px rounded corners */
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

/* Track that holds the slides */
.s2w-slider-track {
  display: flex;
  height: 100%;
  transition: transform 0.5s ease-in-out;
}

/* Individual Slide */
.s2w-slide {
  min-width: 100%;
  height: 100%;
}

.s2w-slide img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

/* Navigation Arrows */
.s2w-arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: rgba(0, 0, 0, 0.5);
  /* 0.5 opacity circle */
  color: white;
  /* White arrow */
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  /* Circle shape */
  font-size: 18px;
  cursor: pointer;
  z-index: 10;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.3s;
}

.s2w-arrow:hover {
  background-color: rgba(0, 0, 0, 0.8);
}

.s2w-prev {
  left: 20px;
}

.s2w-next {
  right: 20px;
}

js:
document.addEventListener('DOMContentLoaded', () => {
  // Updated selectors to match s2w scope
  const track = document.querySelector('.s2w-slider-track');
  const slides = document.querySelectorAll('.s2w-slide');
  const nextBtn = document.querySelector('.s2w-next');
  const prevBtn = document.querySelector('.s2w-prev');

  let currentIndex = 0;
  const totalSlides = slides.length;
  let autoSlideInterval;

  // Function to move the slider
  function updateSlider() {
    const amountToMove = -currentIndex * 100;
    track.style.transform = `translateX(${amountToMove}%)`;
  }

  // Go to next slide
  function nextSlide() {
    currentIndex++;
    if (currentIndex >= totalSlides) {
      currentIndex = 0;
    }
    updateSlider();
  }

  // Go to previous slide
  function prevSlide() {
    currentIndex--;
    if (currentIndex < 0) {
      currentIndex = totalSlides - 1;
    }
    updateSlider();
  }

  // Timer Reset Logic
  function resetTimer() {
    clearInterval(autoSlideInterval);
    startTimer();
  }

  function startTimer() {
    autoSlideInterval = setInterval(nextSlide, 3000); // 3 seconds
  }

  // Event Listeners
  nextBtn.addEventListener('click', () => {
    nextSlide();
    resetTimer();
  });

  prevBtn.addEventListener('click', () => {
    prevSlide();
    resetTimer();
  });

  // Initialize
  startTimer();
});
